package com.bablabd.sadasidhekotha;

import android.support.v4.app.Fragment;
import android.graphics.Typeface;
import android.os.Bundle;
import android.view.LayoutInflater;
import android.view.View;
import android.view.ViewGroup;
import android.view.View.OnClickListener;
import android.widget.TextView;
import android.widget.ZoomControls;

public class BoiBoimelaOOnnanno extends Fragment {
	TextView tv;
	int x = 16;

	public BoiBoimelaOOnnanno() {
		return;
	}

	@Override
	public View onCreateView(LayoutInflater inflater, ViewGroup container,
			Bundle savedInstanceState) {

		View rootView = inflater.inflate(R.layout.ganimuktirbangladesh,
				container, false);
		ZoomControls zoom = (ZoomControls) rootView.findViewById(R.id.zoomc);
		tv = (TextView) rootView.findViewById(R.id.bbltext);
		tv.setTextSize(x);
		zoom.setOnZoomInClickListener(new OnClickListener() {

			@Override
			public void onClick(View v) {
				// TODO Auto-generated method stub
				x = x + 1;
				tv.setTextSize(x);
			}
		});
		zoom.setOnZoomOutClickListener(new View.OnClickListener() {

			@Override
			public void onClick(View v) {
				// TODO Auto-generated method stub

				x = x - 1;
				tv.setTextSize(x);
			}
		});
		TextView titleTv = (TextView) rootView.findViewById(R.id.title);
		titleTv.setText("বই বইমেলা এবং অন্যান্য");

		Typeface tf = Typeface.createFromAsset(this.getActivity().getAssets(),
				"AdorshoLipi.ttf");
		tv.setText("১\nআমার যতগুলো প্রিয় উৎসব আছে তার মাঝে সবচেয়ে প্রিয় একটি হচ্ছে বিশ্ব সাহিত্য কেন্দ্রের পুরস্কার বিতরণী অনুষ্ঠান। পুরস্কার বললেই চোখের সামনে একটা প্রতিযোগিতার দৃশ্য ফুটে উঠে। আমরা ধরে নিই অসংখ্য মানুষ বিশেষ কোনো একটা কিছুর জন্যে এসে অন্যের সঙ্গে প্রতিযোগিতা করছে এবং তার মাঝে মাত্র একজন বা দুইজন অন্য সবাইকে কনুই দিয়ে পিছনে ফেলে দিয়ে পুরস্কারটা ছিনিয়ে নিচ্ছে।অন্যদের কাছে জানি না আমার কাছে পুরস্কার বিতরণী অনুষ্ঠান মানেই একটা দুঃখের অনুষ্ঠান, আশাভঙ্গের অনুষ্ঠান। একজন দুজন পুরস্কার পায়, অন্য সবার আশাভঙ্গ হয়। সেই হিসেবে বিশ্ব সাহিত্য কেন্দ্রের পুরস্কার বিতরণী অনুষ্ঠান একটি অসাধারণ উৎসব। কারণ সেখানে হাজার হাজার কিশোর-কিশোরীর সবাই বিনয়ী, সবাই পুরস্কৃত! এ রকম চমকপ্রদ অনুষ্ঠান সারা পৃথিবীতে কতগুলো আছে আমার জানা নেই– খুব বেশি থাকার কথা নয়।\nএই উৎসবে সবাই যে পুরস্কার পায় শুধু তাই নয়, সেই পুরস্কারটি তাদেরকে দেওয়া হয় একটি অসাধারণ কাজের জন্যে, সেটি হচ্ছে বইপড়া। কেউ কেউ মনে করতে পারে বইপড়া আর এমন কী বিষয়, এর জন্যে পুরস্কার দিতে হবে কেন? হাই জাম্প লং জাম দিয়ে মানুষ পুরস্কার পেতে পারে কিন্তু বই পড়ে কেউ পুরস্কার পাবে কেন?\nকিন্তু আমার ধারণা, বইপড়া একটা অসাধারণ বিষয়। যত দিন যাচ্ছে আমার সেই ধারণাটা আরও পোক্ত হচ্ছে। আমি জানি না সবাই এই বিষয়টা লক্ষ্য করছে কী না, আমরা যখন কিছু একটা পড়ি তখন আসলে কাগজের উপর আঁকি-বুকি করে রাখা কিছু ছোট ছোট চিহ্নের দিকে তাকাই (সেগুলোকে আমরা বর্ণ বা অক্ষর বলি)। সেই চিহ্নগুলো চোখের লেন্সের ভেতর দিয়ে (উল্টো হয়ে) রেটিনার উপর পড়ে। রেটিনা থেকে সেই সিগন্যাল মস্তিষ্কে যায়। মস্তিষ্ক সেগুলোকে বিশ্লেষণ করে। আমরা তখন তার মর্মোদ্ধার করি।তারপর আসল ব্যাপারটা শুরু হয়– যেটা পড়েছি সেটা কল্পনা করি। যার কল্পনাশক্তি যত ভালো সে তত চমৎকারভাবে পুরো বিষয়টা উপভোগ করে। পুরো বিষয়টা আসলে অবিশ্বাস্য রকম উঁচুমানের একটা কাজ, মানুষ ছাড়া আর কারও পক্ষে সেটা করা সম্ভব না। যারা বই পড়ে, নিঃসন্দেহে তারা যারা পড়ে না তাদের থেকে ভিন্ন।\nআমাকে মাঝে মাঝেই অনেকে জিজ্ঞেস করে, ভালো লেখক হওয়ার কোনো একটা শর্টকাট পদ্ধতি আমার জানা আছে কী না। আমি সবসময়েই বলি, ভালো লেখক হওয়ার একমাত্র পদ্ধতি হচ্ছে বই পড়া। যে যত বেশি বই পড়বে সে তত ভালো লিখতে পারবে। আমি এটা জোর দিয়ে বলি, কারণ আমাদের পরিবারের হুমায়ূন আহমেদ এই দেশের একজন অসাধারণ লেখক ছিল, আর সে একেবারে শিশু বয়স থেকে শুধু বই পড়ে আসছে। আমার মনে হয় সে জন্যে সে এত সুন্দর লিখতে পারত।বই পড়ে সবাই যে সফল লেখক হয়ে যাবে তা নয় কিন্তু বই পড়লে নিশ্চিতভাবে নিজের ভেতরে একটা পরিবর্তন হয়। সেই কবে রবীন্দ্রনাথ ঠাকুর মারা গেছেন, জীবনানন্দ দাস ট্রামের তলায় চাপা পড়েছেন। মজার ব্যাপার হল, তাদের লেখাগুলো এখনও পুরোপুরি জীবন্ত। যখন পড়ি তখন মনে হয় তাঁরা বুঝি সামনে বসে আছেন। আমাদের দেশের মানুষের বইপড়ার অভ্যাসটি কম। যত দিন যাচ্ছে মনে হয় অভ্যাসটি আরও কমে যাচ্ছে।\nইউরোপ-আমেরিকার বাস বা রেলস্টেশনে অপেক্ষা করার সময় দেখা যায় সবাই একটা না একটা বই পড়ছে (আজকাল ই-বুক রিডার দিয়েও পড়ে)। খুব যে গভীর জ্ঞানের বই তা নয়, জনপ্রিয় কোনো বই কিন্তু পড়ছে তাতে কোনো সন্দেহ নেই। সেই তুলনায় আমাদের দেশের বাস-ট্রেন-স্টেশনে গেলে দেখতে পাই মানুষজন খুবই বিরস বদনে কিছু না করে চুপচাপ বসে আছে (আজকাল মোবাইল টেলিফোন হয়েছে, তাই হয়তো মোবাইল ফোনে জোরে জোরে কথা বলছে)।কিন্তু বইপড়ার দৃশ্য খুবই কম। আমি লক্ষ্য করে দেখেছি, যদি-বা কেউ বই পড়ে সেটি হবে কমবয়সী ছেলে বা মেয়ে, বড় মানুষ নয়। বড় মানুষেরা পত্রিকা পড়তে পারে। বড়জোর ম্যাগাজিনে চোখ বুলায় কিন্তু বই পড়ে খুব কম।\nসারা পৃথিবীতেই বইয়ের প্রতিশব্দ এখন টেলিভিশন। বইপড়া যে রকম একটা অসাধারণ উঁচুমানের মানসিক প্রক্রিয়া, টেলিভিশন ঠিক সে রকম নিচুমানের মানসিক প্রক্রিয়া! বইপড়ার সময় মস্তিষ্কে যে রকম নানা ধরনের ঘটনা ঘটতে থাকে, টেলিভিশন দেখার সময় তার কিছুই হয় না। আমরা টেলিভিশনের দিকে তাকিয়ে থাকলেই সবকিছু আমাদের মস্তিষ্কে ঢুকে যায়।আমি বেশকিছু কিশোর উপন্যাস লিখেছি। মাঝে মাঝেই টিভির লোকজন আমার সঙ্গে যোগাযোগ করে সেই উপন্যাসগুলো থেকে টিভির উপযোগী নাটক বানানোর অনুমতি চান। আমি কখনও তাদের অনুমতি দিই না। বিনয়ের সঙ্গে বলি, যখন কেউ আমার কিশোর উপন্যাসটি পড়ে, তখন সে চরিত্রগুলোকে নিজের মতো করে কল্পনা করে নিতে পারে। যার কল্পনাশক্তি যত প্রবল তার চরিত্রগুলো তত জীবন্ত। কিন্তু যখন সেটি থেকে টেলিভিশনের জন্যে নাটক (বা সিরিয়াল) তৈরি হবে তখন চরিত্রগুলোকে সে আর কখনও কল্পনা করতে পারবে না, সরাসরি তাদের চোখে আঙুল দিয়ে দেখিয়ে দেওয়া হবে।একজন শিশু, কিশোর যদি কল্পনা করা না শিখল তাহলে তার জীবনের পাওয়ার মতো আর কী থাকল? পৃথিবীর সর্বকালের সর্বশ্রেষ্ঠ বিজ্ঞানী আইনস্টাইন বলেছিলেন, জ্ঞান থেকে গুরুত্বপূর্ণ হচ্ছে কল্পনাশক্তি। এর চাইতে খাঁটি কথা আর কিছু হতে পারে না। জ্ঞান যদি হয় একটা দামি গাড়ি, তাহলে কল্পনাশক্তি হচ্ছে পেট্রল! পেট্রল নামের কল্পনা ছাড়া জ্ঞানের গাড়ি নিশ্চল হয়ে এক জায়গায় পড়ে থাকবে, তাকে দিয়ে কোনো কাজ করানো যাবে না।যারা টেলিভিশনের জন্যে নাটক তৈরি করতে খুবই আগ্রহী, তখন তারা আমাকে বোঝান বই খুব বেশি মানুষ পড়ে না, কিন্তু সবাই টেলিভিশন দেখে। আমি তখন তাদের উল্টো বুঝাই, সে জন্যেই বই নামে একটা বিষয়কে বাঁচিয়ে রাখতে হয়, যেন কেউ কেউ সেটা পড়ে অন্যসব সাধারণ মানুষ থেকে আলাদা হয়ে বড় হতে পারে।\nযারা বই পড়ে তারা অন্য রকম মানুষ। একসময় তারাই দেশ, সমাজ কিংবা পৃথিবীর নেতৃত্বে দেবে। এখন আউট বই পড়ার জন্যে, তারা তাদের বাবা-মা থেকে যতই বকুনি শুনুক, একসময় তারাই হবে গুরুত্বপূর্ণ।\nসে জন্যে বিশ্ব সাহিত্য কেন্দ্রের পুরস্কার বিতরণী অনুষ্ঠানটি আমার খুব প্রিয় একটি উৎসব। আমি যদি সেখানে যাবার সুযোগ পাই তাহলে হাজার হাজার শিশু-কিশোরকে দেখার সুযোগ পাই যারা অন্যদের থেকে ভিন্ন, যারা বই পড়ে। আমি জানি যারা বড় হয়ে তারাই এই দেশকে চালাবে।বইয়ের প্রতিপক্ষ হিসেবে টেলিভিশনের সঙ্গে এখন আরও একটি বিভীষণ যুক্ত হয়েছে, সেটি হচ্ছে কম্পিউটার। কম্পিউটার নাম দেখেই বোঝা যাচ্ছে এটি তৈরি হয়েছিল কম্পিউট বা হিসাব করার জন্য। এখন মাঝে মাঝেই মনে হয় এটি ব্যবহার করে ‘কম্পিউট’ ছাড়া অন্য সব কাজই করা হয়!\nসারা পৃথিবীর সকল মানুষের ভেতরে এখন একটা দুর্ভাবনা কাজ করছে; সেটা হচ্ছে আগে যখন তরুণ প্রজন্ম তার সময়ের একটা অংশ পড়ার জন্যে ব্যবহার করত, বেশিরভাগ সময়েই সেই পড়া ছিল খাঁটি পড়া। এখন সেই পড়ার মাঝে ভেজাল ঢুকে যাচ্ছে। এখন তারা অনেক সময় নষ্ট করে সামাজিক নেটওয়ার্কের অপ্রয়োজনীয় ‘স্ট্যাটাস’ পড়ে! সেই পড়াটিও ভাসা ভাসা, যেটুকু পড়ে তার চাইতে বেশি দেখে। বিষয়টি নূতন, তাই কেউই সঠিকভাবে জানে না এর ফলাফলটা কী হবে।যখন কোনো একটা বিষয় সম্পর্কে কেউ কিছু জানে না, তখন সেটা বিশ্লেষণ করতে হয় কমন সেন্স দিয়ে। আমাদের কমন সেন্স বলে, প্রযুক্তিকে ব্যবহার করতে হয়। যদি প্রযুক্তি আমাদের ব্যবহার করতে শুরু করে তখন বুঝতে হবে কোথাও বড় ধরনের সমস্যা আছে। আমার ধারণা সেটি ঘটতে শুরু করেছে। এই ব্যাপারে প্রযুক্তি আমাদের ব্যবহার করতে শুরু করেছে।\n\n\n২\nবই নিয়ে শুরু করেছিলাম, তাই বই নিয়ে বলতে চাই। আমাদের দেশে ফেব্রুয়ারির বই মেলা নামে একটা অসাধারণ ব্যাপার ঘটে। কেউ যেন ভুলেও মনে না করে এটা বই বিক্রি করার একটা আয়োজন। এটা মোটেও সেটি নয়। আমরা দেখেছি প্রকাশকেরা বিক্রি বাড়ানোর জন্যে মাঝে মাঝে মেলার সময় বাড়িয়ে ফেব্রুয়ারি থেকে ঠেলে মার্চে নিয়ে গেছেন। মজার ব্যাপার হচ্ছে, দর্শকেরা ফেব্রুয়ারি মাসের শেষ দিনটি পর্যন্ত হইচই করে উৎসাহ নিয়ে মেলায় গিয়েছেন কিন্তু মার্চ মাস আসা মাত্রই তারা মেলায় যাওয়া বন্ধ করে দিয়েছেন।বই মেলার একটা নিজস্ব চরিত্র আছে, আমি সেটা বোঝার চেষ্টা করি। অনেক কিছুই বুঝতে পারি না, শুধু অনুভব করতে পারি। আমি দীর্ঘদিন দেশের বাইরে ছিলাম, বই মেলাটি যখন ধীরে ধীরে তার বর্তমান রূপটি নিয়েছে আমি আসলে সেটি দেখিনি। তবে সম্ভবত প্রথম উদ্যোগটি আমরা দেখেছিলাম ঢাকা বিশ্ববিদ্যালয়ের ছাত্র হিসেবে কার্জন হল থেকে হেটে হেঁটে টিএসসি যাবার সময় বাংলা একাডেমিতে উঁকি দিয়ে দেখি কয়েকটা টেবিলে কিছু বই নিয়ে মুক্তধারা নামের একটি প্রকাশনী বসে আছে।সেই বই মেলা এখন বলতে গেলে বিশাল একটা জাতীয় ব্যাপার হয়ে দাঁড়িয়েছে। পাঠক-লেখক-প্রকাশক সবাই এই মেলার জন্যে অপেক্ষা করে থাকে। ভালো ভালো লেখকেরা শুধুমাত্র বই মেলা উপলক্ষে বই লেখার বিষয়টা একেবারেই পছন্দ করেন না। এর মাঝে সৃজনশীলতার অংশটি কম, কুটির শিল্পের অংশটা বেশি। আমি যেহেতু ভালো লেখকদের দলে নই (বাচ্চাকাচ্চাদের জন্যে লেখার মহা সুবিধে, কেউ সিরিয়াসলি নেয় না, যা খুশি করা যায়!), তাই আমি বই মেলা উপলক্ষে লিখি।আমি ব্যাপারটি দিয়ে বলতে পারি যদি ফেব্রুয়ারির বই মেলা না থাকত আমার লেখালেখি বলতে গেলে হতই না! পৃথিবীর সব দেশে একজন লেখক তিন-চার বছর সময় নিয়ে একটা বই লিখেন। আর আমাদের দেশের লেখকদের প্রতি বছর তিন-চারটা বই লিখতে হয়।\nঅন্যদের কথা বলি না, আমার যে লিখতেই হবে আমি সেটা নিশ্চিতভাবে জানি। আমার পাঠক বাচ্চাকাচ্চা, তাদের চাওয়া আসলে সরাসরি হুমকির মতো। তাদের জন্যে কিছু একটা লিখে ফেললে তারা যে ভালোবাসাটুকু দেখায় সেটা এত আন্তরিক যে আমার সেটাকে উপেক্ষা করার কোনো সুযোগ নেই। তাই বিদগ্ধ খাঁটি লেখকেরা আমাদের মতো মৌসুমী লেখকদের নিয়ে যতই হাসি-তামাশা করেন না কেন, আমি এই বই মেলার প্রতি কৃতজ্ঞ। আমার লেখালেখি হয়েছে এই বই মেলার জন্যে।বই মেলায় পাঠক-লেখক-প্রকাশক সবারই নিজস্ব এক ধরনের ভাবভঙ্গি থাকে। বড় লোকের ছেলেমেয়েরা মা-বাবাকে নিয়ে আসে, যেটা কিনতে চায় সেটা ঝটপট কিনে নেয়। আবার দরিদ্র মা তার সন্তানকে নিয়ে এসেছে, বাচ্চাটা একটা বই উল্টেপাল্টে দেখছে, চোখে লোভাতুর দৃষ্টি, মা টাকার হিসেব করে ম্লান মুখে বাচ্চাটিকে মাথা নেড়ে নিষেধ করছে, দেখে বুকটা ভেঙে যায়।অস্বীকার করার উপায় নেই, এই দেশের মানুষের জন্যে আমাদের দেশের বইয়ের দাম অনেক বেশি। আমার কাছে যখন কেউ এই বিষয়টা নিয়ে কথা বলে আমি তাদের সঙ্গে একমত হই। সঙ্গে সঙ্গে তাদেরকে আরেকটা কথা মনে করিয়ে দিই। সেটা হচ্ছে কেউ যদি মনে করে সে শুধুমাত্র বই কিনে পড়বে তাহলে কিন্তু সে খুব বেশি বই পড়তে পারবে না। কোনো একটা শখের বই কিনে সংগ্রহে রাখতে পারে কিন্তু বেশিরভাগ বই তাকে পড়তে হবে না কিনে। লাইব্রেরি থেকে এনে কিংবা পরিচিত বন্ধু-বান্ধবের কাছ থেকে নিয়ে। শৈশবে আমাকে যদি বই কিনে পড়তে হত তাহলে আমি আর কয়টা বই পড়তাম?\n\n৩\nকম দামে বই পড়ার এবং সংগ্রহ করার একটা সুযোগ এসেছে, যদিও সেটা সবাই সমান আগ্রহ নিয়ে গ্রহণ করেনি। সেটি হচ্ছে ই-বুক। বইটি কাগজে ছাপা বই নয়, বইটি উলেকট্রনিক এবং সেটি পড়তে ল্যাপটপে, ট্যাবলেটে কিংবা ই-বুক রিডারে। এমনকি ভালো স্মার্ট ফোনেও এই বই পড়া সম্ভব।\nআমি জানি, অনেকেই আমার কথাটা শুনে নাক কুঁচকে ফেলেছেন, হতাশভাবে মাথা নাড়ছেন এবং বলছেন একটা বই যদি হাত দিয়ে ধরতেই না পারলাম, নূতন বইয়ের ঘ্রাণটাই নিতে না পারলাম, তাহলে সেটি আবার কীসের বই? খুবই খাঁটি কথা। কিন্তু যখন আমি আবিষ্কার করি সারা পৃথিবীর যে কোনো জায়গায় বসে, পৃথিবীর প্রায় যে কোনো বই আমি মুহুর্তের মাঝে নামমাত্র মূল্যে কিনে ফেলতে পারব এবং সেটা বইপড়ার মতো পড়তে পারব– তখন আমার হাত দিয়ে স্পর্শ করার এবং নাক দিয়ে ঘ্রাণ নেবার সুযোগ না থাকাটা মেনে নিতে এখন কোনো অসুবিধে হয় না।আমরা যখন কোথাও গিয়েছি আমার ব্যাগে একটা বা দুটো বই থাকত। এখন যখন কোথাও যাই আমার ই-বুক রিডারে শ’খানেক বই থাকে। প্রয়োজনে আরও কয়েক হাজার বই থেকে যে কোনো বই কিনে ফেলার সুযোগ থাকে। কেউ বিষয়টা মেনে নিক আর নাই নিক, নূতন পৃথিবী কিন্তু খুব দ্রুত পাল্টে যাচ্ছে। আজ থেকে কয়েক বছর পর সব কাগজের বইয়ের পাশাপাশি এই অদৃশ্য ই-বুক জায়গা করে নেবে। লেখাপড়া, জ্ঞান-বিজ্ঞান, গবেষণার জগতে এটা আসলে ঘটে গেছে। বিশেষ কিছু বইয়ের বেলায় কাগজের বই এখন অর্থহীন, প্রায় সব জার্নাল এখন ইলেকট্রনিক।আমরা যারা পুরানো আমলের মানুষ তারা এখনও কাগজের বই আঁকড়ে ধরে রেখেছি কিন্তু নূতন প্রজন্মের মাঝে কোনো গোঁড়ামি নেই, তারা কাটশট নূতন স্টাইলে বই পড়া শুরু করবে। আমাদের প্রধানমন্ত্রী ঘোষণা দিয়েছেন, ভবিষ্যতে স্কুলের ছাত্রছাত্রীদের নূতন বই না দিয়ে একটা ট্যাবলেট দেওয়া হবে সেখানে সকল পাঠ্যবই ডাউনলোড করে দেওয়া হবে!\nবিষয়টা কিন্তু খুবই অবাস্তব কল্পনা নয়। প্রতি বছর প্রায় তিরিশ কোটি নূতন বই ছাপানো থেকে এটা লক্ষগুণ সহজ কাজ। ছোট শিশুরা নূতন পদ্ধতিতে ঝটপট অভ্যস্ত হয়ে যায়। কাজেই ঠিক করে পরিকল্পনা করে একটা দুটা পাইলট প্রজেক্ট হাতে নিলে এই বিপ্লবটি করে ফেলা যেতেই পারে।\nকাজেই সবাইকে মাথায় রাখতে হবে বইয়ের জগতে নূতন একটা পরিবর্তন আসতে যাচ্ছে। যারা পরিবর্তনটুকু গ্রহণ করতে রাজি আছে তারা এ ব্যাপারে থাকবে। অন্যেরা পিছিয়ে যাবে।\nআমাদের দেশেও কিন্তু এই উদ্যোগটি নেওয়া শুরু হয়েছে। প্রতি কয়েক বছর আমার কাছে বেশ কয়েকজন উদ্যোক্তা এসেছেন, আমার সঙ্গে যোগাযোগ করেছেন, তারা আমার একটি দুটি বই ই-বুকে পাল্টে নিয়ে পরীক্ষা-নিরীক্ষাও শুরু করেছেন। এ বছরও একাধিক উদ্যোক্তা এই প্রক্রিয়াটি নিয়ে পরীক্ষা-নিরীক্ষা শুরু করেছেন।বিষয়টা ঠিক কীভাবে অগ্রসর হবে আমরা জানি না। যারা প্রযুক্তিবিদ তারাই কি প্রকাশক হয়ে যাবেন কী না সেটা নিয়েও অনেকের মাঝে সন্দেহ রয়েছে। আমার ধারণা প্রযুক্তিবিদেরা যদি শুধুমাত্র প্রযুক্তির একটা ভিত্তি তৈরি করে দেন আর প্রকাশকেরা কাগজের বইয়ের পাশাপাশি ইলেকট্রনিক বই ছাপানোর দায়িত্ব নিয়ে নেন– তাহলেই এই নূতন বিষয়টা ঘটে যেতে পারে। আমরা অনেক কম খরচে অনেক বই পড়া শুরু করতে পারব।বিশ্ববিদ্যালয়ের লাইব্রেরির জন্যে প্রতি বছর আমাদের আক্ষরিক অর্থে কোটি কোটি টাকার বই কিনতে হয়। বিদেশি একটা বইয়ের দাম দশ-পনর হাজার টাকা পর্যন্ত হতে পারে। আমাদের বিশ্ববিদ্যালয়ের একটি আইসিটি ইনসটিটিউট তৈরি হচ্ছে। তার লাইব্রেরির জন্যে প্রায় তেরো হাজার বই কেনার অর্ডার দেওয়া হয়েছে। গড়ে একটা বইয়ের দাম পড়েছে মাত্র চল্লিশ টাকা! কারণ বইগুলো ই-বুক।ইনসটিটিউট তৈরি শেষ হয়নি, লাইব্রেরির দেওয়াল উঠেনি, সেলফ কেনা হয়নি। তের হাজার বই যে কোনোদিন চলে আসবে। বইগুলো কোথায় রাখা হবে সেটা নিয়ে আমার বিন্দুমাত্র দুশ্চিন্তা নেই। বইগুলো উইয়ে খেয়ে ফেলবে কী না সেটা নিয়েও আমার দুশ্চিন্তা নেই। কোনো দুষ্টু ছেলে বইয়ের পাতা কেটে ফেলবে কী না সেটা নিয়েও দুশ্চিন্তা নেই।\nআমাদের ডাটা সেন্টারের সার্ভারের হার্ড ড্রাইভে তের হাজার বই রাখতে কিংবা সংরক্ষণ করতে আমার এক চিমটিও বাড়তি জায়গা লাগবে না!\nভবিষ্যতের লাইব্রেরি কেমন হবে সেটি কি সবাই কল্পনা করতে পারছে?\n\n\n\n");
		titleTv.setTypeface(tf);
		tv.setTypeface(tf);
		return rootView;
	}
}
