package com.bablabd.sadasidhekotha;


import android.support.v4.app.Fragment;
import android.graphics.Typeface;
import android.os.Bundle;
import android.view.LayoutInflater;
import android.view.View;
import android.view.ViewGroup;
import android.view.View.OnClickListener;
import android.widget.TextView;
import android.widget.ZoomControls;


public class EkDuiTin  extends Fragment {
	TextView tv;
	int x=16;


	public EkDuiTin(){
		return;
	}

	@Override
    public View onCreateView(LayoutInflater inflater, ViewGroup container,
            Bundle savedInstanceState) {

        View rootView = inflater.inflate(R.layout.ganimuktirbangladesh, container, false);
       ZoomControls zoom = (ZoomControls) rootView.findViewById(R.id.zoomc);
        tv = (TextView) rootView.findViewById(R.id.bbltext);
        tv.setTextSize(x);
        zoom.setOnZoomInClickListener(new OnClickListener() {
			
    		@Override
    		public void onClick(View v) {
    			// TODO Auto-generated method stub
    			x=x+1;
    			tv.setTextSize(x);
    		}
    	});
        zoom.setOnZoomOutClickListener(new View.OnClickListener() {
			
    		@Override
    		public void onClick(View v) {
    			// TODO Auto-generated method stub
    			
    			x=x-1;
    			tv.setTextSize(x);
    		}
    	});
       TextView titleTv=(TextView)rootView.findViewById(R.id.title);
       titleTv.setText("এক দুই এবং তিন");
       
        Typeface tf= Typeface.createFromAsset(this.getActivity().getAssets(),"AdorshoLipi.ttf");
        tv.setText("০১.\nআমার ধারণা গত কয়েক সপ্তাহে এই দেশের সকল মানুষের বিশাল একটা অভিজ্ঞতা হয়েছে। অন্যদের কথা জানি না, অনেক বিষয়েই আমার নিজেরই চোখ খুলে গেছে। যে বিষয়গুলো আগে আলাদা করে চোখে পড়েনি, আজকাল তার অনেক কিছুই চোখে পড়তে শুরু করেছে। তবে রাজনীতি এখনও আমার কাছে অনেক জটিল বিষয়। অনেক কিছুই কমনসেন্সে মিলে না, তাই সবকিছু বুঝতে পারি না। তারপরেও আমি এই জটিল এবং দুর্বোধ্য বিষয়টাকে নিজের মতো করে বুঝে নিয়েছি। এই মুহুর্তে আমি মাত্র তিনটি মাপকাঠি দিয়ে বাংলাদেশের রাজনীতিকে নিজের কাঝে বোঝানোর চেষ্টা করে যাচ্ছি। আমার কাছে মাপকাঠিগুলো এরকম:\nপ্রথমটি অবশ্যই বাংলাদেশকে নিয়ে। আজকাল মাঝে মাঝেই আমার মনে হয়, অনেকেই বুঝি বাংলাদেশের আসল ব্যাপারটাই ভুলে গেছেন। অনেকের ধারণা গাছে পেকে যাওয়ার পর আম যেভাবে টুপ করে নিচে এসে পড়ে, বাংলাদেশটাও বুঝি সেভাবে তাদের হাতে এসে পড়েছে। তাই মানুষ যেভাবে আম খায়, তারাও বুঝি সেভাবে কেটে কেটে ঝাল মরিচ দিয়ে কিংবা চটকে চটকে দুধ দিয়ে কিংবা চিপে চিপে রস বের করে শকিয়ে আমসত্ব বানিয়ে খেতে পারবে। ব্যাপারটা মোটেও সেরকম নয়। বাংলাদেশটা আমরা পেয়েছি রীতিমত একটা যুদ্ধ করে, আর সেটাও রাজায় রাজায় যুদ্ধ ছিল না। সেটা ছিল গণমানুষের যুদ্ধ। সেই যুদ্ধে এই দেশের মানুষেরা যেভাবে প্রাণ দিয়েছিল, তার কোনো তুলনা নেই। তাই যারা প্রাণ দিয়ে, রক্ত দিয়ে যুদ্ধ করে এই দেশটা এনে দিয়েছে তার যে স্বপ্ন দেখেছিল সেটাই হচ্ছে বাংলাদেশ। তাই এই দেশের রাজনীতি হোক, অর্থনীতি হোক, লেখাপড়া হোক, চাষ আবাদ হোক, গানবাজনা হোক, সুখ-দুঃখ মান-অভিমান হোক, কোনো কিছুই মুক্তিযুদ্ধের স্বপ্নের বাইরে হতে পারবে না। অর্থাৎ বাংলাদেশের রাজনীতির প্রথম মাপকাঠি হচ্ছে ‍মুক্তিযুদ্ধ। যারা এটিকে অস্বীকার করে তাদের এই দেশে রাজনীতি করা দূরে থাকুক, এই দেশের মাটিতে রাখার অধিকার নেই।\nঅবশ্যই মুক্তিযুদ্ধ নিয়ে আমাদের বুকের ভেতরে এক ধরণের তীব্র আবেগ রয়েছে কিন্তু কেউ যেন মনে না করে, এটা শুধু একটা অর্থহীন আবেগ। আমাদের বাংলাদেশের ভবিষ্যতটুকুও রয়েছে এই মুক্তিযুদ্ধে। আজ থেকে প্রায় চল্লিশ বছর আগে যখন বাংলাদেশের জন্ম হয়েছিল, তখন এই দেশটিকে তলাবিহীন ঝুড়ি বলা হয়েছিল। এখন বাংলাদেশকে কেউ তলাবিহীন ঝুড়ি বলে না। পাশের দেশ ভারত এখন সরাসরি মার্কিন যুক্তরাষ্ট্রের সঙ্গে টেক্কা দেওয়ার সাহস রাখে। অমর্ত্য সেন সেই ভারতের সঙ্গে বাংলাদেশের তুলনা করে বলেছেন, আমরা অনেক দিক দিয়ে ভারত থেকে এগিয়ে। বাংলাদেশের সাফল্যের রহস্যটি বোঝার জন্য রীতিমত একাডেমিক গবেষণা করা হয়। আর সেই গবেষণার ফলাফল আমাদের কাছে অবাক করা বিষয় নয়, আমরা সেটা বহুদিন থেকে জানি। একটি হচ্ছে মুক্তিযুদ্ধ করে দেশ স্বাধীন করা আত্মবিশ্বাসী একটা জাতির পরিচয়, অন্যটি হচ্ছে হাজার বছর থেকে ঘরের ভেতর আটকে রাখা মেয়েদের ঘরের বাইরে এসে সবার সাথে কাজ করে দেওয়ার সুযোগ। কেউ কী লক্ষ্য করেছে, জামায়াতে ইসলামী আর হেফাজতে ইসলামের প্রধান এলার্জি ঠিক এই দুটো বিষয়ে। যে দুটো শক্তি নিয়ে আমরা এগিয়ে যাব, ঠিক সেই দুটো শক্তির বিরুদ্ধে যুদ্ধ করে তারা আমাদের পিছনে ঠেলে দিতে চায়?\nকেউ যেন মনে না করে মুক্তিযুদ্ধে স্বপ্ন, আদর্শ, চেতনা, এই বিষয়গুলো শুধু এক ধরণের আবেগ এবং মোটামুটি একটা বিমূর্ত বিষয়। আমাদের বাহাত্তরের সংবিধানে মুক্তিযুদ্ধে স্বপ্নগুলো যত্ন করে তুলে ধরা হয়েছিল। (কারো যদি কৌতুহল হয়, তাহলে তারা বাহাত্তরের সংবিধানটি পড়ে দেখতে পারে।) একটু একটু করে যখন সেই সংবিধানের কাটাছেঁড়া করা হয়েছে, প্রতিবার আমাদের হৃদয়ে রক্তক্ষরণ হয়েছে। আমরা স্বপ্ন দেখি, আবার আমরা একটি সেই বাহাত্তরের সংবিধানে ফিরে যাব।\nতাই যখন আমরা শুনতে পাই কেউ ঘোষণা করছে, বাহাত্তরের সংবিধানে এই দেশের মানুষের আশা আকাঙ্খার প্রতিফলন হয়নি, তখন আমি অবাক হয়ে যাই। না, মুক্তিযুদ্ধের অবমাননা করার দুঃসাহস দেখে আমি অবাক হই না। আমি অবাক হই রাজনৈতিক নির্বুদ্ধিতা দেখে। এই দেশে মুক্তিযুদ্ধেকে অস্বীকার করে আর কেউ কখনও রাজনীতি করতে পারবে না। কেউ যদি আনুষ্ঠানিকভাবে মুক্তিযুদ্ধকে অস্বীকার করে, তাহলে বুঝতে হবে এই মানুষটির আর যে ক্ষমতাই থাকুক, বাংলাদেশের মানুষকে রাজনৈতিক নেতৃত্ব দেওয়ার ক্ষমতা নেই। সে তার রাজনৈতিক দলের সম্পদ নয়, তার দলের বোঝা, তার দলের জঞ্জাল।গত কয়েক সপ্তাহে আমি যেসব বিষয় জানতে পেরেছি, তার একটা আমার জন্য খুব গুরুত্বপূর্ণ। সেটি হচ্ছে- বাংলাদেশের প্রতি কিছু বিদেশি কূটনীতিকদের অসম্মানজনক ব্যবহার। বাংলাদেশের প্রতি বিদেশিদের প্রচ্ছন্ন তাচ্ছিল্যের হাত থেকে বাঁচার জন্য আমি একদিন বিদেশ ত্যাগ করে নিজের দেশে চলে এসেছিলাম। এখন সেই আমার দেশেই সেই বিদেশি কূটনীতিকদের অবমান সহ্য করতে হয়। আমি এখনও বিশ্বাস করতে পারি না যে, তাদের একটা দল বিজয় দিবসে আমাদের স্মৃতিসৌধে যায়নি।\nআমি যতদূর জানি, আমাদের বাংলাদেশ এখন বিদেশিদের সাহায্যের উপর সেভাবে নির্ভর করে না। এখনও এদেশে নিশ্চয়ই অনেক টাকা-পয়সা আসে এবং সেগুলো আসে বিভিন্ন এনজিও-দের কাছে। আমি এরকম একটা এনজিও-এর বোর্ড অফ ডিরেক্টরদের একজন সদস্য হিসেবে তাদের বড় কর্মকর্তার বেতন ঠিক করে দিয়েছিলাম। পাবলিক বিশ্ববিদ্যালয়ের একজন প্রফেসর হিসেবে আমি তখন যত বেতন পাই, সেই বেতনটি ছিল তার চার থেকে পাঁচগুণ। কাজেই এনজিও-এর কর্মকর্তারা নিশ্চয়ই ভালই থাকেন এবং যে দেশ থেকে তাদের বেতন ভাতা আসে, সেই দেশের প্রতি তাদের নিশ্চয়ই এক ধরণের কৃতজ্ঞও থাকে। কাজেই সেই দেশের এজেন্ডাগুলো নিশ্চয়ই সোজাসুজি কিংবা পরোক্ষভাবে বাস্তবায়নের একটা চাপ থাকে। তাই তারা তাদের নির্ধারিত কাজ ছাড়াও বাড়তি কাজ করেন। এই দেশের মানুষকে ফ্রি উপদেশ দেন। সেটি সমস্যা নয়, আমরা সবাই উপদেশ দিতে পছন্দ করি। কিন্তু ঠিক সেই সময় দেশটি ভয়ঙ্কর সন্ত্রানে বিপর্যস্ত, মানুষকে পুড়িয়ে মারার হোলি উৎসব চলছে, রেললাইন তুলে ফেলে ট্রেনকে ফেলে দেওয়া হচ্ছে, রাস্তা কেটে ফেলা হচ্ছে, পুলিশকে পিটিয়ে মারা হচ্ছে। আমাদের এনজিও কর্মকর্তারা এই ভয়ঙ্কর সন্ত্রাস বন্ধ করায় কথা বললেন না, তারা সরকারকে নির্বাচন বন্ধ করার উপদেশ দিলেন। নির্বাচন বন্ধ করার জন্য এই দেশে ভয়ঙ্কর সন্ত্রাস চলছিল, তাই প্রকারান্তরে তারা সন্ত্রাসেরই পক্ষ নিলেন।\nএই ব্যাপারটা আমাকে খুব আহত করেছে। আমি জানি আমাদের দেশের এনজিওগুলো অসাধারণ কাজ করে। আমি তাদের অনেকের বোর্ড অফ ডিরেক্টরসদের সদস্য। তারা মাঝে মাঝে আমাকে কোনো একটা বিষয় নিয়ে লেখালেখি করতে বলেন। আমার কাছে যখন সেটা গুরুত্বপূর্ণ মনে হয় আমি তখন লেখি। কিন্তু এখন সবকিছু এলোমেলো হয়ে গেছে। এই মুহুর্তে আমার সম্পর্ক কেটে ফেলার সময় হয়েছে, আমার শ্রমটুকু হয়তো দেওযা উচিৎ দীনহীন দুর্বল প্রতিষ্ঠা বা স্বেচ্ছাসেবকদের। যারা নিজেদের যেটুকু সামর্থ্য আছে, তাই দিয়ে ধুকে ধুকে চলছে। তারা যতই দুর্বল হোক, তারা আমাদের দেশের প্রতিষ্ঠান। যারা আমার দেশকে অপমান করে তাদের কাছ থেকে তারা কোনো টাকা নেয় না। নিজের পায়ে দাঁড়ানোর নিশ্চয়ই এক ধরণের গৌরব আছে।\nএই দেশের রাজনীতিতে আমার চাওয়া খুবই কম। যে দলটি দেশ চালাবে, সে হবে মুক্তিযুদ্ধে স্বপ্নে বিশ্বাসী। একই সাথে যে দলটি বিরোধী দল হিসেবে থাকবে, সেটিও হবে মুক্তিযুদ্ধে বিশ্বাসী। শুধু এই বিষয়টা নিশ্চিত করতে পারলে দেশের সকল মানুষ নিশ্চিন্তে ঘুমোতে পারতো। সরকার পরিবর্তন হলেও কারো মনে বিন্দুমাত্রা দুর্ভাবনা থাকবে না। একটি ভিন্ন দল দেশকে চালানোর দায়িত্ব পাবে কিন্তু দেশটুকু অগ্রসর হবে একই গতিতে।\nঅর্থাৎ বাংলাদেশের রাজনীতির প্রথম মাপকাঠি হচ্ছে মুক্তিযুদ্ধ। যে মুক্তিযুদ্ধের স্বপ্নকে বিশ্বাস করে না, তার এই দেশে রাজনীতি করার অধিকার নেই।\nদ্বিতীয় মাপকাঠি নিয়ে আমার ভেতরে বিন্দুমাত্র দ্বিধা নেই। সেটি হচ্ছে, আমাদের দেশে হিন্দু বা অন্যান্য ধর্মের মানুষজনের নিরাপত্তা দেওয়ার অঙ্গীকার। গত কয়েকদিন এই দেশের হিন্দু সম্প্রদায়ের উপর যে আঘাত নেমে এসেছে, তার চাইতে লজ্জা এবং অপমানের বিষয় আর কিছু হতে পারে না। আমি মুসলমান পরিবারে জন্ম নিয়েছি, তাই এই দেশে আমার বেঁচে থাকার নিরাপত্তা আছে, আমার তো একটি হিন্দু পরিবারেও জন্ম হতে পারতো। আমি কোথায় জন্ম নেব, সেখানে তো আমার কোনো ভূমিকা নেই। একটি শিশু ঘটনাক্রমে একটি হিন্দু পরিবারে জন্ম নিয়েছে বলে তার জীবনের কোনো নিরাপত্তা থাকবে না, আমরা কেমন করে সেটি ঘটতে দিলাম? খবরের কাগজে যখন একজন ভীত মায়ের কোলে একজন শিশু অসহায় মুখটি দেখি, আমি প্রচণ্ড অপরাধবোধে ভুগতে থাকি। আমার মনে হয়, এর জন্য নিশ্চয়ই কোনো কোনোভাবে আমরাই দায়ী।\nযারা এটি করে তাদের মস্তিষ্ক কীভাবে কাজ করে আমার জানা নেই। এর মাঝে শুধু যে ধর্ম নিয়ে সাম্প্রদায়িকতা আছে তা নয়। একটি হিন্দু পরিবারকে কোনোভাবে তাদের বাস্তুভিটা থেকে উৎখাত করতে পারলে তার জায়াগাটা দখল করে নেওয়ার সুযোগ আছে। সেই ব্যাপারটিতে শুধু জামায়াত-বিএনপি আছে তা নয়, আওয়ামী লীগের লোকজনও আছে। পত্রপত্রিকায় মাঝে মাঝে নেতাদের সাথে সাথে তাদের ছবি ছাপা হয়। কাজেই যতক্ষণ পর্যন্ত এই মানুষগুলোকে খোঁজে বের করে তাদেরকে শাস্তি দেওয়া না হয় কিংবা যতক্ষণ পর্যন্ত এরকম ঘটনা যেন আর কখনও না ঘটে, সেই বিষয়টা নিশ্চিত করা না হয় এই দেশের হিন্দু ধর্মাবলম্বী মানুষেরা আমাদের ক্ষমা করবে না। শুধু পুলিশ-র‍্যাব দিয়ে বাড়ি পাহারা দিয়ে তাদেরকে রক্ষা করার পরিকল্পনা করা যথেষ্ট নয়। আসলে সেই এলাকার মানুষজনকেও দায়িত্ব নিতে হবে। আগে একটা সময় ছিল, যখন রাজনৈতিক দল বা সামাজিক সংগঠন এগুলো করতো। এখন সেটি আর ঘটতে দেখি না। এখন আমরা ভয়ঙ্কর একটা ঘটনা ঘটতে দেই, তারপর সেই ঘটনার প্রতিবাদে বড় শহরে একটা মানববন্ধন, একটা সেমিনার করে আমাদের দায়িত্ব শেষ করে ফেলি।\nআমাদের আরও এক ধাপ অগ্রসর হতে হবে। আমাদের দেশের মানুষের চিন্তা-ভাবনারও পরিবর্তন করতে হবে। একটা সময় ছিল যখন মানুষ কী ভাবছে সেটা বোঝার জন্য তার সাথে সামনা-সামনি কথা বলতে হতো। এখন সামাজিক নেটওয়ার্কগুলো হওয়ার কারণে কাজটা সহজ হয়েছে, কে কী ভাবছে সেটা নেটওয়ার্কে তাদের কথাবার্তা মন্তব্য দেখে বোঝা যায়। আমরা এক ধরণের আতঙ্ক নিয়ে আবিষ্কার করেছি, আপাত দৃষ্টিতে শিক্ষিত মার্জিত রুচিশীল অনেক মানুষের ভেতরটাও আসলে কুৎসিত সাম্প্রদায়িক ভাবনা দিয়ে অন্ধকার হয়ে আছে। আমার উনিশশ’ একাত্তর সালের একটা ঘটনার কথা মনে আছে। একটা অসহায় হিন্দু পরিবার প্রাণ বাঁচানোর জন্য ছুটে যাচ্ছে, আমার মা তাদের একটু অর্থ সাহায্য করার চেষ্টা করলেন। আমরা যে পরিবারের বাসায় আশ্রয় নিয়েছি, তাদের একজন আমার মা’কে বলল, ‘বিধর্মী মানুষকে সাহায্য করলে কোনো সওয়াব হবে না। যদি সাহায্য করতেই চান, তাহলে একজন বিপদগ্রস্থ মুসলমানকে সাহায্য করেন! শুনে শুধু আমার মা নয়, আমরা সবাই হতভম্ব হয়ে গেলাম!\nসেই তেতাল্লিশ বছর আগের এই দেশের কিছু কিছু মানুষের চিন্তা-ভাবনার বড় ধরণের কোনো পরিবর্তন হয়েছে বলে মনে হয় না। হয়তো নিজে নিজে কোনো কিছুরই পরিবর্তন হয় না, পরিবর্তনের চেষ্টা করতে হয়। আমাদের দেশে যেন ধর্মান্ধ সাম্প্রদায়িক মানুষের সংখ্যা বাড়তে না থাকে, সেজন্য আমাদের হয়তো দীর্ঘ পরিকল্পনা করতে হবে। স্কুলের বাচ্চাদের জীবনটা শুরু করতে হবে সকল ধর্মের জন্য ভালোবাসার কথা শুনে। শিল্পী সাহিত্যিক কবিদের হয়তো বলতে হবে মানুষের কথা, মানুষে মানুষে যে কোনো ভেদাভেদ নেই, সেই সত্যটির কথা। টেলিভিশনে নাটক লিখতে হবে, ছায়াছবি তৈরি করতে হবে। সবচেয়ে বড় কথা একজন মানুষ নিজে অসাম্প্রদায়িক হলেই চলবে না, দায়িত্ব নিতে হবে তার আশপাশে যারা আছে সবাইকে অসাম্প্রদায়িকতার সৌন্দর্য্যটুকু বোঝানোর।তাই আমি এখন অত্যন্ত নিশ্চিতভাবে জানি, আমাদের দেশের সকল রাজনৈতিক দলের দায়িত্ব এই দেশে হিন্দু সম্প্রদায়কে একটি নিশ্চিন্ত নির্ভাবনার দেশ উপহার দেওয়া। যে তারাও এই দেশটিকে তাদের নিজের দেশ বলে ভাবতে পারে। ডিজিটাল বাংলাদেশ না হলে ক্ষতি নেই, পদ্মা সেতু না হলেও ক্ষতি নেই, যানজটমুক্ত বাংলাদেশ না হলেও ক্ষতি নেই, নিরবিচ্ছিন্ন বিদ্যুৎ প্রবাহ না হলেও ক্ষতি নেই, যদি এই সরকার (কিংবা অন্য যে কোনো সরকার) এই দেশের হিন্দু ধর্মাবলম্বী বা অন্য ধর্মাবলম্বী সকল মানুষদের একটি নিশ্চিন্ত নির্ভাবনার দেশ উপহার দিতে পারে।আমার হিসেবে মুক্তিযুদ্ধের উপর বিশ্বাস এবং অসাম্প্রদায়িক বাংলাদেশের অঙ্গীকারের পর তৃতীয় মাপকাঠিটি হচ্ছে আদি ও অকৃত্রিম নৈতিকতা। যে মানুষটি রাজনীতি করবে, তাকে সৎ হতে হবে এবং এর মাঝে কোনো ছাড় দেওয়ার সুযোগ নেই। এবারে নির্বাচনের সময় প্রার্থীরা তাদের সম্পদের হিসেব দিয়েছিলেন। পত্রপত্রিকাগুলো তাদের নিজেদের দেওয়া হিসাবগুলোতেই হুবহু ছাপিয়ে দিয়েছিল। আর সেটা নিয়ে শুধু সারাদেশ নয়, সামাজিক নেটওয়ার্কের কল্যাণে সারা পৃথিবীতেই বিশাল একটা প্রতিক্রিয়া হয়েছিল। যারা তীব্র সমালোচনার মুখে পড়েছেন, তারা প্রথমে তথ্যগুলো চাপা দেওয়ার চেষ্টা করেছেন, তারপর নানাভাবে বিষয়টা ব্যাখ্যা করার চেষ্টা করেছেন কিন্তু খুব একটা লাভ হয়নি। সাধারণ মানুষ বুঝতে ভুল করে না, সবচেয়ে বড় কথা যাদেরকে সবাই সৎ মানুষ বলে জানে তাদের সম্পদ তো হঠাৎ বেড়ে যায়নি। তাদেরকে তো কিছু ব্যাখ্যাও করতে হয়নি। সাধারণ মানুষ বুঝতে ভুল করে না। সবচেয়ে বড় কথা যাদেরকে সবাই সৎ মানুষ বলে জানে, তাদের সম্পদ তো হঠাৎ করে বেড়ে যায়নি। তাদেরকে তো কিছু ব্যাখ্যাও করতে হয়নি। তাই আসলে কী ঘটেছে, সবাই বুঝে গেছে।কিছু আগে সিটি করপোরেশনের নির্বাচনের সময় অনেক চেষ্টা করেও আওয়ামী লীগের প্রার্থীরা কোথাও নির্বাচিত হতে পারেনি। এটাকে নানা ধরণের ষড়যন্ত্রের ফর্মূলা দিয়ে ব্যাখ্যা করার চেষ্টা করা হলেও সত্যি কথাটি হচ্ছে, সাধারণ মানুষ তাদের ভোট দেয়নি। শিক্ষা, স্বাস্থ্য, কৃষি, খাদ্য, বিদ্যুৎ, যোগাযোগ এরকম ব্যাপারগুলোতে সরকার যথেষ্ট ভালো কাজ করলেও কেন তাদের কেউ ভোট দিল না, সেটা নিয়ে আমার একটু কৌতূহল ছিল। আমার কোনো গোপন সূত্র নেই কিন্তু পরিচিত অপরিচিত মানুষের সাথে কথা বলে মোটামুটিভাবে বোঝা গেছে, সাধারণ মানুষ তাদের আশপাশে যে সব ছাত্রলীগ, যুবলীগ ও আওয়ামী লীগের নেতাকর্মীদের দেখেছে, দৈনন্দিন জীবনে তাদের কারণে যেসব হেনস্থা সহ্য করতে হয়েছে, দুর্নীতি-চাঁদাবাজির শিকার হতে হয়েছে, সেগুলো তাদের মনকে বিষিয়ে দিয়েছে। একশ’টা পদ্মাসেতু, এক হাজারটা হলমার্ক কেলেঙ্কারি আওয়ামী লীগের যে ক্ষতি করত, একটি বিশ্বজিৎ হত্যা তার থেকে বেশি ক্ষতি করেছে।\nদুর্নীতি কিংবা অসততার কোনো কিছুই গ্রহণযোগ্য নয়। যারা রাজনীতি করে তাদের সৎ হতেই হবে। এটি নতুন পৃথিবী, কোনো কিছুই আর গোপন থাকে না। কেন দুর্নীতিবাজ, কে সন্ত্রাসী, কে গডফাদার, সামাজিক নেটওয়ার্ক দিয়ে সেটা মুহুর্তের মাঝে সারা পৃথিবীতে জানাজানি হয়ে যায়। কাজেই আমাদের আগামী বাংলাদেশে আমরা আর দুর্নীতিবাজ রাজনৈতিক নেতা দেখতে চাই না। \n\n০২.\nআমি কী চাই, সেটা আমি নিজেকে বলতে পারি। যারা আমার পরিচিত তাদেরকে বলতে পারি, যারা আমার কথা শুনতে চায় তাদেরকে জোর করে শোনাতে পারি। কিন্তু যাদের কাছে আমরা সেটা চাই, সেই রাজনীতিবিদরা কী আমাদের সেটা দেবে? তারা কী আমাদের চাওয়া-পাওয়াকে কোনো গুরুত্ব দেয়?\nদেওয়ার কথা নয়। ভুল হোক শুদ্ধ হোক, তাদের অনেক আত্মবিশ্বাস। কিন্তু আমার ব্যক্তিগত ধারণা এই মুহুর্তে সারা পৃথিবীর সাথে সাথে বাংলাদেশও একটা খুব গুরুত্বপূর্ণ সময়ে পৌঁছেছে। আমরা দিল্লীর নির্বাচনে দেখেছি, আম আদমী পার্টি নামে একটা তরুণদের রাজনৈতিক দল সব হিসেব ওলট-পালট করে ক্ষমতায় চলে এসেছে। যেহেতু বাংলাদেশে বিশাল একটা তরুণদের দল আছে, অনেক হিসেবে তারা ভারতবর্ষের তরুণদের থেকে বেশি রাজনীতি সচেতন। তাই তারা চাইলেই কী এই দেশের রাজনীতির জগতেও একটা ওলট-পালট করে ফেলার ক্ষমতা রাখে না?\nআমাদের এতো কষ্টের এতো ভালোবাসার দেশকে আমরা যেভাবে চাই, যদি সেভাবে গড়ে তোলা না হয় তাহলে কী এই দেশেও নতুন একটা রাজনৈতিক শক্তি গড়ে উঠতে পারে না? যার চালিকা হবে নতুন প্রজন্ম। আগামী এক দুই পাঁচ বছরে না হোক, তারপরেও কী হতে পারে না? তাদের তো হারানোর কিছু নেই, দেওয়ার অনেক কিছু আছে।\n\n\n\n");
        titleTv.setTypeface(tf);
        tv.setTypeface(tf);
        return rootView;
    }
}
