package com.bablabd.sadasidhekotha;

import android.support.v4.app.Fragment;
import android.graphics.Typeface;
import android.os.Bundle;
import android.view.LayoutInflater;
import android.view.View;
import android.view.ViewGroup;
import android.view.View.OnClickListener;
import android.widget.TextView;
import android.widget.ZoomControls;

public class TomrajaraShibir  extends Fragment {
	TextView tv;
	int x=16;


	public TomrajaraShibir(){
		return;
	}

	@Override
    public View onCreateView(LayoutInflater inflater, ViewGroup container,
            Bundle savedInstanceState) {

        View rootView = inflater.inflate(R.layout.ganimuktirbangladesh, container, false);
       ZoomControls zoom = (ZoomControls) rootView.findViewById(R.id.zoomc);
        tv = (TextView) rootView.findViewById(R.id.bbltext);
        tv.setTextSize(x);
        zoom.setOnZoomInClickListener(new OnClickListener() {
			
    		@Override
    		public void onClick(View v) {
    			// TODO Auto-generated method stub
    			x=x+1;
    			tv.setTextSize(x);
    		}
    	});
        zoom.setOnZoomOutClickListener(new View.OnClickListener() {
			
    		@Override
    		public void onClick(View v) {
    			// TODO Auto-generated method stub
    			
    			x=x-1;
    			tv.setTextSize(x);
    		}
    	});
       TextView titleTv=(TextView)rootView.findViewById(R.id.title);
       titleTv.setText("তোমরা যারা শিবির করো");
       
        Typeface tf= Typeface.createFromAsset(this.getActivity().getAssets(),"AdorshoLipi.ttf");
        tv.setText("বেশ কিছুদিন আগের কথা। আমি আমার অফিসে যাচ্ছি, তখন বারান্দায় আমার দুজন ছাত্রের সঙ্গে দেখা হলো, তারা আমাকে কিছু বলল না কিন্তু তাদের দেখে আমার মনে হলো, তারা আমার সঙ্গে কথা বলতে চায়। আমি জিজ্ঞেস করলাম, ‘তোমরা কি আমার সঙ্গে কথা বলতে চাও?’ তারা মাথা নাড়ল, একজন কুণ্ঠিতভাবে আমার হাতে দুটি বই তুলে দিয়ে বলল, ‘স্যার, আপনাকে এই বই দুটি দিতে এসেছি।’ আমি বই দুটি নিলাম। বিজ্ঞানের ওপর চমৎকার দুটি বই, হাতে নিয়ে বললাম, ‘থ্যাংকু। সুন্দর পাবলিকেশন্স।’ তারপর বই দুটি খুললাম, ভেতরে লেখা ইসলামী ছাত্রশিবির।\n মুহূর্তে আমার সারা শরীর শক্ত হয়ে গেল। ১৯৭১ সালে জামায়াতে ইসলামী পাকিস্তান সেনাবাহিনীর আজ্ঞাবহ হয়ে এই দেশে যে ভয়ংকর হত্যাযজ্ঞ চালিয়েছে, সে জন্য আমি তাদের কখনো ক্ষমা করিনি। আমি জেনেশুনে কখনো কোনো জামায়াতে ইসলামীর নেতার সঙ্গে হাত মেলাইনি। আমার যে আপনজনেরা মুক্তিযুদ্ধে মারা গিয়েছে, তাদের সম্মান দেখানোর জন্য এটি আমার ব্যক্তিগত সিদ্ধান্ত। আমেরিকান এম্বাসির এক অনুষ্ঠানে আমন্ত্রিত হয়ে যখন আবিষ্কার করেছি, সেখানে জামায়াতে ইসলামীর নেতাদেরও ডাকা হয়েছে, আমি সেখান থেকে উঠে চলে এসেছিলাম। আমি আমার এই বিশ্বাসের কথা কখনো গোপন রাখিনি। কাজেই এই দুজন ছাত্র সেটা জানে না, তা হতে পারে না।\n আমি ছাত্রদের বই দুটি ফেরত দিয়ে অত্যন্ত কঠিন গলায় বললাম, ‘জামায়াতে ইসলামীকে আমি কোন চোখে দেখি, তোমরা জানো না? তোমরা সেই দলের মানুষ হয়ে তোমাদের সংগঠনের বই আমাকে উপহার দিতে এসেছ? তোমরা আমাকে চেনো না?’\n ছাত্র দুটির চেহারা বিবর্ণ হয়ে গেল। আমি মুক্তিযুদ্ধের সময় বদর বাহিনীর প্রধান হয়ে নিজামী আর মুজাহিদ কী করেছে, তাদের মনে করিয়ে দিলাম। গোলাম আযম যুদ্ধের সময় কী করেছে এবং বাংলাদেশের জন্মের পরও কীভাবে তারা বিরোধিতা করেছে, সেই কথা বললাম। আমার মতো শিক্ষকেরা জামায়াতে ইসলামীর ছাত্রসংগঠনের সদস্যদের তৈরি বদর বাহিনীর হাতে কীভাবে মারা গিয়েছে, সেই ঘটনাগুলো বলে তাদের কাছে জানতে চাইলাম, কম বয়সী তরুণ হওয়ার পরও তারা কেমন করে যুদ্ধাপরাধীদের একটা সংগঠনের সদস্য হতে পারল?\nএকজন ছাত্র দুর্বল গলায় বলল, ‘স্যার, আমরা তো জামায়াতে ইসলামী করি না। আমরা ছাত্রশিবির করি।’\n অনেক দিন আগের কথা, জামায়াতে ইসলামী আর ছাত্রশিবিরের মধ্যে পার্থক্যটুকু নিয়ে আমি তাদের কী বলেছিলাম, আমার এখন মনে নেই। শুধু মনে আছে, ছাত্র দুটি মাথা নিচু করে আমার কাছ থেকে ফিরে গিয়েছিল।\n নানা কারণে এই ঘটনার কথা আমি ভুলতে পারি না। আমি ছোট ছেলেমেয়েদের জন্য লেখালেখি করি। আমি মনে-প্রাণে বিশ্বাস করি, আমাদের দেশের ছেলেমেয়েরা একটি নতুন বাংলাদেশের সন্তান এবং তারা বড় হয়ে আমাদের দেশটাকে পাল্টে দেবে। আমি যখন সেই কথাটা তাদের বলি, আমার ধারণা, তারা আমার কথা বিশ্বাস করে। তাই তাদের অনেকেই আমার কাছে উৎসাহের কথা, অনুপ্রেরণা কিংবা স্বপ্নের কথা শুনতে আসে। শিবিরের এই দুটি ছেলে নিশ্চয়ই ভেবেছিল, তাদের এই চমৎকার বই দুটি আমাকে মুগ্ধ করবে, আমি উৎসাহসূচক কিছু বলব। অন্য দশজন তরুণের মতো তারাও এক ধরনের দাবি নিয়ে আমার কাছে এসেছিল, কিন্তু আমি তাদের আশা পূরণ করতে পারিনি। আমার ভয়ংকর রকমের তীব্র প্রতিক্রিয়া দেখে তারা নিশ্চয়ই হতবুদ্ধি হয়ে গিয়েছিল—কিন্তু আমার কিছু করার ছিল না।\n আমি তাদের কথাগুলোও ভুলতে পারি না। তারা আমাকে বলেছিল যে তারা জামায়াতে ইসলামী করে না, তারা শিবির করে। তাহলে তারা কি সত্যিই বিশ্বাস করে যে তারা জামায়াতে ইসলামী থেকে ভিন্ন? ১৯৭১ সালে এই দেশে জামায়াতে ইসলামী যে পৈশাচিক হত্যাকাণ্ড করেছে, যে অমানুষিক নির্যাতন করেছে, যে ভয়ংকর হত্যাকাণ্ড করেছে, সেগুলো তাদের কোনোভাবে স্পর্শ করে না?\n এই দুজন ছাত্র ছাড়া আর কখনোই কোনো জামায়াত বা শিবিরকর্মী আমার কাছে কথা বলতে আসেনি, তাই আমি কোনো দিন হয়তো এই প্রশ্নগুলোর উত্তর খুঁজে পাব না।\n \n\n২.\nকেউ যদি আমাকে জিজ্ঞেস করে, এই দীর্ঘ জীবনে আমি সবচেয়ে বিচিত্র, সবচেয়ে অবিশ্বাস্য বিষয় কী দেখেছি। আমি এতটুকু দ্বিধা না করে বলব, সেটি হচ্ছে ইসলামী ছাত্রশিবির। তার কারণ, যে বয়সটি হচ্ছে মাতৃভূমিকে ভালোবাসার বয়স, সেই বয়সে তারা ভালোবাসে দেশদ্রোহী বিশ্বাসঘাতকদের, যারা এই মাতৃভূমির বিরুদ্ধে যুদ্ধ করেছে। যে বয়সে একজন তরুণের মুক্তিযোদ্ধাদের বীরত্বে অনুপ্রাণিত হওয়ার কথা, সেই বয়সে তারা অনুপ্রাণিত হয় সেই মুক্তিযোদ্ধাদের হত্যাকারীদের দিয়ে। যে বয়সে তাদের স্বপ্ন দেখার কথা দেশের বড় বড় লেখক, শিল্পী, ডাক্তার, বিজ্ঞানী, সাংবাদিককে নিয়ে, সেই বয়সে তারা আনুগত্য মেনে নিয়েছে সেই সব মানুষের, যারা আলবদর বাহিনী তৈরি করে একাত্তরে এই দেশের লেখক, শিল্পী, ডাক্তার, বিজ্ঞানী আর সাংবাদিকদের হত্যা করেছে! যে বয়সে তাদের একুশে ফেব্রুয়ারিতে প্রভাতফেরি করার কথা, ষোলোই ডিসেম্বরে স্মৃতিসৌধে ফুল দেওয়ার কথা, পয়লা বৈশাখে রাজপথে রবীন্দ্রসংগীত গাওয়ার কথা, সেই বয়সে তারা যে শুধু এই অবিশ্বাস্য আনন্দ থেকে নিজেদের বঞ্চিত করে রাখে তা নয়, তারা এগুলোকে প্রতিহত করার চেষ্টা করে। যে বয়সে তাদের মুক্তচিন্তা শেখার কথা, গান গাওয়ার কথা, নাটক করার কথা, আদর্শ নিয়ে ভাবালুতায় ডুবে যাওয়ার কথা, সেই সময় তারা ক্ষুদ্র গণ্ডির মধ্যে নিজেদের আটকে রাখতে শেখে, সাম্প্রদায়িক হতে শেখে, ধর্মান্ধ হতে শেখে। যে বয়সে ছেলে আর মেয়ের ভেতর সহজ ভালো লাগা ভালোবাসা জন্ম নেওয়ার কথা, সেই বয়সে তারা সেই অনুভূতিগুলোকে অশ্রদ্ধা করতে শেখে—সে জন্য তারা কত দূর যেতে পারে, সেটা আমি নিজের চোখে দেখেছি, সেই ভয়ংকর কাহিনি আমি কখনো কাউকে বলতেও পারব না!\nযখন এই বাংলাদেশের সব মানুষ দেশের যুদ্ধাপরাধীদের বিচারের জন্য অপেক্ষা করছে, তখন ইসলামী ছাত্রশিবির নামে এই সংগঠনের হতভাগ্য তরুণদের পথে নামিয়ে দেওয়া হয়েছে পুলিশের সঙ্গে যুদ্ধ করার জন্য। খবরের কাগজ খুললেই দেখতে পাই, এখনো দেশের আনাচকানাচ থেকে তাদের ধরে জেলে ঢোকানো হচ্ছে। আমার খুব জানার ইচ্ছে করে যে নেতারা তাদের বুঝিয়েছে, রাস্তায় নেমে চোরাগোপ্তা হামলা করে পুলিশের গাড়ি পোড়াতে হবে, নিজের ভবিষ্যৎকে বিপন্ন করতে হবে। সেই সব নেতা কি তাদের সন্তানদেরও পথে নামিয়েছে? আমি মোটামুটি নিশ্চিত, সেটি ঘটেনি। আমি আগেও দেখেছি, এই নেতারা যখন তাদের কর্মী বাহিনীকে অনিশ্চিত জীবনের দিকে ঠেলে দেয়, তখন তাদের সন্তানেরা ইংরেজি মিডিয়াম প্রাইভেট বিশ্ববিদ্যালয়ে পড়ে নিজেদের ভবিষ্যৎ গড়ে তোলে।\nআমি অনেক চিন্তা করেছি, কিন্তু কিছুতেই বুঝতে পারিনি, কেমন করে বাংলাদেশের মতো রক্তস্নাত একটি দেশে, যেখানে মুক্তিযোদ্ধারা নৃশংস পাকিস্তান সেনাবাহিনীর সঙ্গে রক্তক্ষয়ী যুদ্ধ করে দেশ স্বাধীন করেছে, সেখানে একজন মানুষ মুক্তিযুদ্ধ কিংবা মুক্তিযোদ্ধাদের ভালো না বেসে তাদের হত্যাকারীদের ভালোবাসতে পারে! আমার মনে আছে, আমি বহুকাল পরে যখন প্রথম এই দেশে ফিরে এসেছিলাম, তখন ইসলামী ছাত্রশিবিরের একটা মিছিল দেখে একধরনের অবিশ্বাসের দৃষ্টিতে তাদের দিকে তাকিয়ে ছিলাম। তখন লক্ষ করেছিলাম, একজন ছাত্র তার হাতের ফাইল দিয়ে নিজের মুখটি ঢেকে রেখেছে, যেন আমি তার মুখটা দেখতে না পারি। আমার সামনে এই পরিচয় দিতে তার লজ্জা কিন্তু এই মিছিল থেকে তার বের হয়ে যাওয়ার ক্ষমতা নেই—এর চেয়ে দুঃখের ব্যাপার আর কী হতে পারে?\nএকজন ছাত্র কেমন করে শিবির করে, তার একটি উত্তর অবশ্য আমি একবার খুঁজে পেয়েছিলাম। আমাদের বিশ্ববিদ্যালয়ের একজন ছাত্র একবার আমাকে একটি এসএমএস করে জানিয়েছিল যে সে মুক্তিযোদ্ধার সন্তান, খুব ভালো ছাত্র এবং তার বিশ্ববিদ্যালয়ের শিক্ষক হওয়ার খুব ইচ্ছে। তার বিভাগীয় প্রধান জামায়াতে ইসলামীর লোক এবং তাকে পরিষ্কারভাবে জানিয়ে দেওয়া হয়েছে যে সে যদি শিবির না করে, তাহলে তাকে শিক্ষক হতে দেওয়া হবে না। সে জন্য সে শিবিরে যোগ দিয়েছে এবং এটি নিয়ে তার কোনো অহংকার নেই। সেই এসএমএসটিতে আমি একজন মেরুদণ্ডহীন অসহায় হতভাগা মানুষকে আবিষ্কার করেছিলাম। তার জন্য কোনো মমতা নয়, আমি করুণা অনুভব করেছিলাম। আমি ইচ্ছে করলেই সেই ছাত্রটিকে খুঁজে বের করতে পারতাম, তার নীতিহীন বিভাগীয় প্রধানের পরিচয় জানতে পারতাম কিন্তু আমি তার কিছুই করিনি—আমার রুচি হয়নি।\n আমার মাঝেমধ্যে জানার ইচ্ছে করে, এ ধরনের কারণে কতজন তরুণ শিবিরে যোগ দিয়েছে—কোনো স্বপ্ন নয়, কোনো আদর্শ নয়, শুধু স্বার্থ, শুধু চাওয়া-পাওয়া। মাঝেমধ্যেই পত্রপত্রিকায় দেখতে পাই, জামায়াতে ইসলামীর নাকি অনেক অর্থবিত্ত, তাদের অনেক ধরনের ব্যবসা। এই দলে যোগ দিলে নাকি তাদের প্রতিষ্ঠানে চাকরি পাওয়া যায়। বিশ্ববিদ্যালয়ে তারা হল দখল করে রাখে, তাদের দল করলে সেই হলে সিট পাওয়া যায়। তারা কলেজ দখল করে রাখে, তাদের দল করলে সেই কলেজে ভর্তি হওয়া যায়। পত্রপত্রিকায় দেখি, পরিচিতদের কাছে শুনি, তাদের দল নাকি অত্যন্ত সংগঠিত। আদর্শ ছাড়া কিংবা ভুল আদর্শের সংগঠন কি খুব বেশি দিন টিকে থাকতে পারে? দীর্ঘদিন মিলিটারির শাসনে থাকার কারণে মানুষ যখন বিভ্রান্ত ছিল, তখন এই দেশে জামায়াতে ইসলামীরা ইলেকশনে ৩০টার মতো সিট পেয়েছিল। (কী লজ্জা!) যখন দেশের মানুষ গণতন্ত্রের ছোঁয়া পেতে শুরু করেছে, একটু বুঝতে শুরু করেছে তখন তাদের সিটের সংখ্যা এক-দুইয়ে নেমে এসেছিল। উপায় না দেখে তখন তারা বিএনপির ঘাড়ে চড়ে বসেছে, আবার তারা গোটা ত্রিশেক সিট পেয়েছে, মন্ত্রী পর্যন্ত হয়েছে। দেশের মানুষ যখন আবার সজাগ হয়েছে, তখন সিটের সংখ্যা আবার এক-দুইয়ে নেমে এসেছে। এখন তারা কার ঘাড়ে উঠবে। এই দেশে যদি নির্বাচন করেই শুধু ক্ষমতায় যাওয়া যায়, তাহলে তাদের জন্য কোন পথটুকু খোলা আছে। আমার খুব আশা ছিল, যুদ্ধাপরাধীদের বিচার নিয়ে দেশের মানুষের এত আগ্রহ, এত উত্তেজনা দেখে বিএনপি হয়তো যুদ্ধাপরাধীদের এই দলটিকে পরিত্যাগ করবে—তারা করেনি। আমি খুব আশাহত হয়েছি কিন্তু তাদের ছাত্রসংগঠন আমাকে আশাহত করেনি। তারা শিবিরের সঙ্গে হাত মেলাতে রাজি হয়নি।\nআমি রাজনীতি ভালো বুঝি না, আমার রাজনৈতিক বিশ্লেষণ কারও গুরুত্ব দিয়ে নেওয়ার প্রয়োজন নেই। কিন্তু আমি একটা বিষয় খুব ভালো করে জানি, এই দেশে মুক্তিযুদ্ধকে অস্বীকার করে আর কেউ কোনো দিন রাজনীতি করতে পারবে না। পঁচাত্তর থেকে নব্বইয়ের সেই কালো সময় আমরা পার হয়ে এসেছি, আর কেউ কখনো এই দেশের মানুষকে সেই অন্ধকার জগতে ঠেলে পাঠাতে পারবে না। কাজেই যুদ্ধাপরাধীদের বাঁচানোর চেষ্টা করে কেউ সুবিধে করতে পারবে না, বরং যত তাড়াতাড়ি সম্ভব তাদের বিচার করে এই গ্লানিময় অধ্যায়কে চিরদিনের মতো সমাপ্ত করে দিতে হবে।\n \n\n৩.\nআমার এই লেখাটি তোমরা যারা শিবির করো, তাদের জন্য। আমি জানি, এটি সম্পূর্ণ অর্থহীন একটি কাজ—আমার এই লেখাটি তোমাদের মধ্যে বিন্দুমাত্র প্রভাব ফেলবে না এবং তোমরা যারা পড়ছ তারা আমার এই লেখায় বিভিন্ন অংশের বিরুদ্ধে এর মধ্যে নানা ধরনের যুক্তি দাঁড় করিয়েছ। শুধু তা-ই নয়, তোমাদের প্রিয় জায়গা—ইন্টারনেটে সম্ভবত এই লেখার বিরুদ্ধে বিশাল একটা প্রচারণা শুরু হবে। কিন্তু তবু আমার মনে হয়েছে, আমার এই কাজটুকু করা উচিত, তোমাদের কখনো যে সত্য কথাগুলো বলা হয়নি, আমার সেটা বলা উচিত।\nতোমাদের কাছে আমার প্রথম যে প্রশ্ন সেটি হচ্ছে, তোমরা কি জানো আবুল আলা মওদুদী নামে যে মানুষটির চিন্তাধারার ওপর নির্ভর করে জামায়াতে ইসলামী নামে রাজনৈতিক দলটি গড়ে উঠেছে, সেই মানুষটিকে মানুষ হত্যার প্ররোচনা দেওয়ার অপরাধে মৃত্যুদণ্ড দেওয়া হয়েছিল (যদিও সেটি শেষ পর্যন্ত কার্যকর করা হয়নি)। তোমরা কি জানো জামায়াতে ইসলামী ইসলাম প্রচারের দল নয়, এটি রাজনৈতিক দল এবং এটি সব সময় ভুল রাজনীতি করে এসেছে? এই উপমহাদেশে যখন ব্রিটিশদের বিদেয় করে পাকিস্তান সৃষ্টি করার আন্দোলন হয়েছে, তখন তারা সেই আন্দোলনের বিরোধিতা করেছে। আবার যখন এই দেশে পাকিস্তান নামের দানবকে পরাস্ত করে বাংলাদেশের স্বাধীনতার জন্য মুক্তিযুদ্ধ হয়েছে, তখন তারা বাংলাদেশের বিপক্ষে গিয়ে পাকিস্তানের পক্ষ নিয়েছে? এখন যখন মুক্তিযুদ্ধের সময় যুদ্ধাপরাধ করে থাকা দেশদ্রোহীদের বিচার করা হচ্ছে, তখন আবার জামায়াতে ইসলামী সেই সত্যকে অস্বীকার করে যুদ্ধাপরাধীদের পক্ষ নিয়েছে—সেটি ঘটেছে তোমাদের চোখের সামনে এবং তোমরা খুব ভালো করে জানো, সেখানে তোমাদের হূদয়হীনভাবে ব্যবহার করা হচ্ছে।\n আমার ধারণা, তোমরা যারা শিবির করো, তারা সম্ভবত কখনোই খোলা মন নিয়ে এই দেশের ইতিহাস সম্পর্কে বাইরের কারও সঙ্গে কথা বলো না। তোমরা সব সময়ই নিজেদের সঙ্গে নিজেরা কথা বলো, একে অন্যকে উৎসাহ দাও, একে অন্যের ওপর নির্ভর করো কিন্তু তোমাদের দলের বাইরের মানুষেরা তোমাদের সম্পর্কে কী ভাবে, কখনোই তার খোঁজ নাওনি। যদি খোঁজ নিতে, তাহলে হয়তো তোমরা সম্পূর্ণ ভিন্ন একটা ছবি দেখতে পেতে। তোমরা সবিস্ময়ে আবিষ্কার করতে, তোমাদের যেভাবে যা কিছু শেখানো হয়েছে, তার সবকিছু সত্যি নয়। তোমরা নিশ্চয়ই লক্ষ করেছ, এই দেশের অজস্র সংবাদপত্র, টেলিভিশন চ্যানেলের মধ্যে তোমাদের দলের দু-একটি পত্রিকা বা টেলিভিশন চ্যানেল ছাড়া অন্য কোথাও তোমাদের সম্পর্কে একটিও ভালো কথা ছাপা হয় না। কিছুদিন থেকে গাড়ি ভাঙচুর বা পুলিশকে আক্রমণ করার যে নতুন কর্মকাণ্ড শুরু করেছ, সেটি করে তোমরা যে নিজেরাই বিপদগ্রস্ত হতে শুরু করেছ, সেটা কি লক্ষ করেছ? আজ রাতেই আমি খবরে জানতে পারলাম, সাধারণ মানুষ তোমাদের ধাওয়া করছে, তোমাদের আক্রমণ করছে। আমি মোটামুটি নিশ্চিত, এটি ধীরে ধীরে আরও বাড়তে থাকবে। তোমরা নিজেদের জন্য যে জীবন বেছে নিয়েছ, তার মধ্যে কি বিন্দুমাত্র মর্যাদা আছে? আত্মতুষ্টি আছে?\nআজ থেকে কয়েক যুগ আগেও এই পৃথিবী যে রকম ছিল, এখন সেই পৃথিবী নেই। এই পৃথিবী অনেক পাল্টে গেছে। নতুন পৃথিবী তালেবান বা লস্কর-ই-তাইয়েবার পৃথিবী নয়। জামায়াতে ইসলামী বা শিবসেনার পৃথিবীও নয়। নতুন পৃথিবী হচ্ছে মুক্তচিন্তার পুরোপুরি অসাম্প্রদায়িক একটা পৃথিবী। এই নতুন পৃথিবীর মানুষেরা অসাধারণ, তারা একে অন্যের ধর্মকে সম্মান করতে শিখেছে, একে অন্যের সংস্কৃতি আর ঐতিহ্যকে উপভোগ করতে শিখেছে, একে অন্যের চিন্তাকে মূল্য দিতে শিখেছে। এই নতুন পৃথিবীতে মানুষে মানুষে কোনো বিভাজন নেই। দেশ-জাতির সীমারেখা পর্যন্ত ধীরে ধীরে উঠে যাচ্ছে।\n তাই এই নতুন পৃথিবীতে যখন কেউ ধর্ম দিয়ে মানুষকে বিভাজন করে রাজনীতি করতে চায়, পৃথিবীর মানুষ তখন তাকে পরিত্যাগ করে। জামায়াতে ইসলামীর মতো বা শিবসেনার মতো রাজনৈতিক দল তাই হচ্ছে বাতিল হয়ে যাওয়া রাজনৈতিক দল—নতুন পৃথিবীতে এই দলগুলোর কোনো ভবিষ্যৎ নেই।\n আমি জানি, যদিও আমি এই লেখাটি লিখেছি যারা শিবির করে তাদের উদ্দেশে কিন্তু তারা আসলে আমার একটি কথাও বিশ্বাস করবে না। যদি বিশ্বাস করেও ফেলে, তার পরও তাদের কিছু করার থাকবে না। এ ধরনের রাজনৈতিক দল যখন তৈরি করা হয়, তখন অনেক লোভ দেখিয়ে দলে টানা হয়। কিন্তু দলে যোগ দিয়ে যদি মোহভঙ্গও হয়, তবু তারা আর দল থেকে বের হতে পারে না। অভিশপ্ত প্রেতাত্মার মতো এক অন্যকে আঁকড়ে ধরে টিকে থাকতে হয়।\nযারা এখনো শিবিরে যোগ দেয়নি, তারা হয়তো এই লেখাটি পড়ে একটুখানি ভাববে। যখন তাকে এই দলে যোগ দেওয়ার কথা বলবে, হয়তো তারা একটিবার চিন্তা করবে, আমাদের এই ভালোবাসার দেশটিকে যারা টুঁটি চেপে হত্যা করতে চেয়েছিল, আমি কেন সেই দলে যোগ দেব? দেশকে যখন ভালোবাসার কথা, তখন কেন আমি দেশের সঙ্গে বেইমানি করব?\nমাতৃভূমিকে ভালোবাসার তীব্র আনন্দ যারা উপভোগ করেনি, যারা ভবিষ্যতেও কোনো দিন অনুভব করতে পারবে না, আমি সেসব হতভাগ্য মানুষের জন্য গভীর করুণা অনুভব করি। \n০৭-১২-২০১২\n\n\n\n");
        titleTv.setTypeface(tf);
        tv.setTypeface(tf);
        return rootView;
    }
}

